        public string GerarPalavra(int tamanhoMaximo, int tamanhoMinimo, bool somenteMaiscula)
        {
            var palavras =  String.Join(" ", textos.Select(item => Formata(item)).Distinct().ToArray()).Split(" ");

            var palavrasFiltradas = new List<string>();

            foreach (var item in palavras)
            {
                bool deveAdicionar = true;
                if(tamanhoMaximo > 0 && item.Length > tamanhoMaximo)
                {
                    deveAdicionar = false;
                }

                if (tamanhoMinimo > 0 && item.Length < tamanhoMinimo)
                {
                    deveAdicionar = false;
                }

                if (deveAdicionar)
                {
                    palavrasFiltradas.Add(item);
                }
            }

            var indice = new Random().Next(palavrasFiltradas.Count - 1);
            return somenteMaiscula == false? palavrasFiltradas[indice] : palavrasFiltradas[indice].ToUpper();
        }

        private string Formata(string text)
        {
            var SEPARADOR = string.Empty;
            var SEPARADOR_ESPACO = " ";
            return text.Replace("[", SEPARADOR).Replace("]", SEPARADOR).Replace("+", SEPARADOR).Replace("»", SEPARADOR).Replace("«", SEPARADOR).Replace(";", SEPARADOR).Replace("•", SEPARADOR).Replace(",", SEPARADOR).Replace(".", SEPARADOR).Replace("(", SEPARADOR).Replace(")", SEPARADOR).Replace("”", SEPARADOR).Replace("\"", SEPARADOR).Replace("“", SEPARADOR).Replace(":", SEPARADOR).Replace("\r\n", SEPARADOR_ESPACO); 
        }
    }
